import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import { Setsingle } from '../Redux/Slice';
import { useNavigate } from 'react-router-dom';

const ProductList = () => {
  const [allproducts, setAllproducts] = useState([]);
  const [catagery, setCatagery] = useState([]);
  const selectedCategory = useSelector((state) => state.cata);
  const searchQuery = useSelector((harii) => harii.Search);
  const singleproduct = useSelector((hloo) => hloo.Single);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await axios.get('https://fakestoreapi.com/products');
        setAllproducts(data);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    };
    fetchData();
  }, []);

  // console.log(allproducts,"all productas")
  // const buynow = (item) => {
  //   dispatch(Setsingle(item));
  //   navigate('/single');
  // };

  return (
    <div style={{ marginTop: '120px' }}>
      <>
        <div className="mycard">
          {allproducts.map((item) => (
            <Card className="mycard2 mx-2 my-3" style={{ width: '18rem', height: '30rem' }}>
              <Card.Img className="cardimages" variant="top" style={{ width: '7rem', height: '7rem', justifyContent: 'center', marginLeft: '30%' }} src={item.image} />
              <Card.Body>
                {/* <Card.Text className="text-center">
                  <h3 style={{ color: 'GrayText' }}>
                    <span style={{ color: 'red', fontSize: '20px', marginBottom: '10px' }}>&#8377;</span> {item.price}
                  </h3>
                </Card.Text> */}
                {/* <Button style={{ marginLeft: '30%' }} variant="success" onClick={() => buynow(item)}>
                  Buy Now
                </Button> */}
              </Card.Body>
            </Card>
          ))}
        </div>
      </>
    </div>
  );
};

export default ProductList;
