import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import { Setsingle } from '../Redux/Slice';
import { useNavigate } from 'react-router-dom';

const ProductList = () => {
  const [allproducts, setAllproducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null); // Track selected product
  const selectedCategory = useSelector((state) => state.cata);
  const searchQuery = useSelector((harii) => harii.Search);
  const singleproduct = useSelector((hloo) => hloo.Single);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await axios.get('https://fakestoreapi.com/products');
        setAllproducts(data);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    };
    fetchData();
  }, []);

  const buynow = (item) => {
    dispatch(Setsingle(item));
    navigate('/single');
  };

  const showProductDetails = (product) => {
    setSelectedProduct(product);
  };

  const closeProductDetails = () => {
    setSelectedProduct(null);
  };

  return (
    <div style={{ marginTop: '120px', display: 'flex' }}>
      <div style={{ flex: 1 }}>
        <div className="mycard">
          {allproducts.map((item) => (
            <Card
              key={item.id}
              className="mycard2 mx-2 my-3"
              style={{ width: '18rem', height: '30rem' }}
              onClick={() => showProductDetails(item)}
            >
              <Card.Img
                className="cardimages"
                variant="top"
                style={{
                  width: '7rem',
                  height: '7rem',
                  justifyContent: 'center',
                  marginLeft: '30%',
                }}
                src={item.image}
              />
              <Card.Body>
                <Button
                  style={{ marginLeft: '30%' }}
                  variant="success"
                  onClick={() => buynow(item)}
                >
                  Buy Now
                </Button>
              </Card.Body>
            </Card>
          ))}
        </div>
      </div>

      <div style={{ flex: 1, marginLeft: '20px' }}>
        {selectedProduct && (
          <Card>
            <Card.Img
              className="cardimages"
              variant="top"
              style={{ width: '7rem', height: '7rem', justifyContent: 'center' }}
              src={selectedProduct.image}
            />
            <Card.Body>
              <Card.Title>{selectedProduct.title}</Card.Title>
              <Card.Text>{selectedProduct.description}</Card.Text>
              <Button onClick={closeProductDetails}>Close</Button>
            </Card.Body>
          </Card>
        )}
      </div>
    </div>
  );
};

export default ProductList;
