import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import { Setsingle } from '../Redux/Slice';
import { useNavigate } from 'react-router-dom';
import Product from './Product';
import ProductDetails from './ProductDetails';

const ProductList = () => {
  const [allproducts, setAllproducts] = useState([]);
  const [selectProduct,setSelectProduct ] =useState(false)
  const [hoveredProduct, setHoveredProduct] = useState(null);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await axios.get('https://fakestoreapi.com/products');
        setAllproducts(data);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    };
    fetchData();
  }, []);

  const handleProductHover = (product) => {
    setHoveredProduct(product);
  };

  
  const handleProductMouseLeave = () => {
    setHoveredProduct(null);
  };

  

  return (
    <div className="product-list" style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap' }}>
    {allproducts.map((item) => (
      <div key={item.id} style={{ margin: '10px' }}>
        <Card
          style={{ width: '20rem', height: '30rem' }}
          onMouseEnter={() => handleProductHover(item)}
          onMouseLeave={handleProductMouseLeave}
        >
          <Card.Img src={item.image} style={{ height: '15rem', objectFit: 'cover' }} />
          {hoveredProduct && hoveredProduct.id === item.id && (
            <ProductDetails data={hoveredProduct} />
          )}
        </Card>
      </div>
    ))}
  </div>
);
};

export default ProductList;
