import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import { Setsingle } from '../Redux/Slice';
import { useNavigate } from 'react-router-dom';

const ProductList = () => {
  const [allproducts, setAllproducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null); 
  const selectedCategory = useSelector((state) => state.cata);
  const singleproduct = useSelector((hloo) => hloo.Single);
  const [selectProduct,setSelectProduct ] =useState(false)
  const navigate = useNavigate();
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await axios.get('https://fakestoreapi.com/products');
        setAllproducts(data);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    };
    fetchData();
  }, []);

  

  return (
    <div style={{ marginTop: '120px' }}>
    <div className="product-list" style={{ display: 'flex', flexWrap: 'wrap' }}>
      {allproducts.map((item) => (
        <div style={{ display: 'flex' }}>
          <Card className="ml-4 my-5 mx-5" style={{ width: '12rem', height: '25rem' }}>
            <Card.Img src={item.image} />
            <Card.Body>
              {/* Add product details here */}
            </Card.Body>
          </Card>
        </div>
      ))}
    </div>
  </div>
  );
};

export default ProductList;
