import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import { Setsingle } from '../Redux/Slice';
import { useNavigate } from 'react-router-dom';

const ProductList = () => {
  const [allproducts, setAllproducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null); 
  const selectedCategory = useSelector((state) => state.cata);
  const singleproduct = useSelector((hloo) => hloo.Single);
  const [selectProduct,setSelectProduct ] =useState(false)
  const navigate = useNavigate();
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await axios.get('https://fakestoreapi.com/products');
        setAllproducts(data);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    };
    fetchData();
  }, []);

  // console.log(allproducts,"all productas")
  // const buynow = (item) => {
  //   dispatch(Setsingle(item));
  //   navigate('/single');
  // };

  const selection = () =>{
   setSelectProduct(true)
  }
  const showProductDetails = (product) => {
    setSelectedProduct(product);
  };

  const closeProductDetails = () => {
    setSelectedProduct(null);
  };

  return (
    <div style={{ marginTop: '120px' }}>
      <>
        <div className="">
          {allproducts.map((item) => (
            <button onClick={selection} className='bg-none'>
            <Card className=" mx-2 my-3" style={{ width: '20rem', height: '30rem' }}>
              <Card.Img src={item.image} />
              <Card.Body>
                {/* <Card.Text className="text-center">
                  <h3 style={{ color: 'GrayText' }}>
                    <span style={{ color: 'red', fontSize: '20px', marginBottom: '10px' }}>&#8377;</span> {item.price}
                  </h3>
                </Card.Text> */}
                {/* <Button style={{ marginLeft: '30%' }} variant="success" onClick={() => buynow(item)}>
                  Buy Now
                </Button> */}
              </Card.Body>
            </Card>
            </button>
          ))}
        </div>
      </>
    </div>
  );
};

export default ProductList;
