import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import { Setsingle } from '../Redux/Slice';
import { useNavigate } from 'react-router-dom';
import Product from './Product';
import ProductDetails from './ProductDetails';

const ProductList = () => {
  const [allproducts, setAllproducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await axios.get('https://fakestoreapi.com/products');
        setAllproducts(data);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    };
    fetchData();
  }, []);

  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  return (
    <div style={{ marginTop: '120px', display: 'flex' }}>
      <div className="product-list" style={{ flex: 1, display: 'flex', flexWrap: 'wrap' }}>
        {allproducts.map((item) => (
          <div style={{ display: 'flex' }} key={item.id}>
            <Card
              className="ml-4 my-5 mx-5"
              style={{ width: '12rem', height: '25rem', border: 'none' }}
              onClick={() => handleProductClick(item)}
            >
              <Card.Img src={item.image} />
              <Card.Body>
                <p>{item.title}</p>
                <p>${item.price}</p>
              </Card.Body>
            </Card>
          </div>
        ))}
      </div>
      <div style={{ flex: 1 }}>
        {selectedProduct && <ProductDetails data={selectedProduct} />}
      </div>
    </div>
  );
};

export default ProductList;
